var appUnlogin = angular.module('unlogin_h5', ['ui.router']);
appUnlogin.config(["$stateProvider","$urlRouterProvider",function($stateProvider,$urlRouterProvider) {
  $urlRouterProvider.otherwise('/login');
  $stateProvider
    .state('login',{
        url: '/login',
        templateUrl: '/static/app/view/login.html',
        controller: 'loginController'
    })
    .state('reg',{
        url: '/reg',
        templateUrl: '/static/app/view/reg.html',
        controller: 'regController'
    })
  }
]);
var appLogin = angular.module('login_h5',['ui.router']);
appLogin.config(["$stateProvider","$urlRouterProvider",function($stateProvider,$urlRouterProvider){
    $urlRouterProvider.otherwise('/home');
    $stateProvider
    .state('home',{
        url: '/home',
        templateUrl: '/static/app/view/home.html',
        controller: 'homeController'
    })
}]);
appLogin.controller('homeController',['$scope','$http','$state',function($scope,$http,$state){
	$http({
		url: '/Res/getAllRes',
		method: 'post'
	}).success(function(res){
		console.log(res);
	});
	$scope.logout = function(){
		$http({
			url: '/User/logout',
			method: 'get'
		}).success(function(res){
			if( res.resultCode == '000000' ){
				alert('退出成功');
				$state.go('login');
			}else{
				alert(res.resultMsg);
			}
		});
	}
}])
appUnlogin.controller('loginController', ['$scope','$http','$state',function($scope,$http,$state){
    $scope.login = function(flag){
        if( flag ){
            $http({
                url: '/User/login',
                method: 'post',
                data: {
                    phone: $scope.phone,
                    password: $scope.password
                }
            }).success(function(res){
                if(res.resultCode == '000000'){
                    alert('登录成功');
                    $state.go('home');
                }else{
                    alert(res.resultMsg);
                }
            })
        }
    }
}])
appUnlogin.controller('regController', ['$scope','$http','$state','$rootScope', function($scope,$http,$state,$rootScope){
    $scope.reg = function(flag){
        if( flag ){
            $http({
                url: '/User/reg',
                method: 'post',
                data: {
                	name: $scope.name,
                    phone: $scope.phone,
                    password: $scope.password,
                    passwordRepeat: $scope.passwordRepeat
                }
            }).success(function(res){
                if( res.resultCode == '000000' ){
                    alert('注册成功');
                    $rootScope.username = res.result.username;
                    $state.go('home');
                }else{
                    alert(res.resultMsg)
                }
            })
        }
    }
}])